    public void CentrarVentana (JInternalFrame form){
        
        dpaneFondo.add(form);
        Dimension dim = dpaneFondo.getSize();
        Dimension dfr = form.getSize();
        form.setLocation ((dim.width-dfr.width)/2, (dim.height-dfr.height)/2);
        form.setVisible(true);
    }        
    
    
    
   
